operate.query:
SELECT * FROM ( SELECT A.*, ROWNUM RN
FROM (
    SELECT * FROM EMP
    WHERE state  <>  1 ORDER BY EMPNO
) A WHERE ROWNUM  <=  {0} ) WHERE RN >= {1}

SELECT * FROM ( SELECT A.*, ROWNUM RN
FROM (
SELECT * FROM dept
WHERE state  <>  1 ORDER BY DEPTNO
) A WHERE ROWNUM  <=  {0} ) WHERE RN >= {1}
